variables.input:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name }
| fieldsAdd host.name
| fieldsRemove timeframe, interval, uptime

0.query:
timeseries {
  cpu_usage=max(system.cpu.utilization)
}, by: {host.name, state}
,filter: { host.name == $Hosts and state == "user"}
| fieldsRemove interval
| sort timeframe[end] desc
| limit 1
| fieldsAdd al=arrayLast(cpu_usage)*100

3.query:
timeseries avg(system.filesystem.utilization), by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd FileSystemUtilization = arrayAvg(`avg(system.filesystem.utilization)`)
| summarize sum(FileSystemUtilization) * 100

6.query:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name, os.type }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd host.name, os.type
| fieldsRemove timeframe, interval, uptime
| limit 1

8.query:
timeseries utilization = avg(process.memory.utilization), 
by: { process.name, host.name },
filter: { host.name == $Hosts }

11.query:
timeseries {
  paging_usage=avg(system.paging.usage)
}, by: {device, state, host.name}
,filter: { host.name == $Hosts }

13.query:
timeseries {
  count = avg(system.cpu.logical.count)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd value.A = arrayAvg(count)
| fieldsAdd host.name
| fieldsRemove timeframe, interval
| limit 1

