variables.input:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name }
| fieldsAdd host.name
| fieldsRemove timeframe, interval, uptime

0.query:
timeseries {
  cpu_usage = max(system.cpu.load_average.1m)
},
filter: { host.name == $Hosts }
| fieldsAdd usage = arrayLast(cpu_usage) * 100

1.query:
timeseries {
	usage = max(system.memory.utilization)
},
by: { host.name }
,filter: { state == "used" }
| fieldsAdd MemoryUtilization = arrayLast(usage) * 100

3.query:
timeseries avg(system.filesystem.utilization), by: { host.name }, filter: { matchesValue(host.name, $Hosts) and mountpoint == "/" }
| fieldsAdd FileSystemUtilization = arrayAvg(`avg(system.filesystem.utilization)`)
| summarize sum(FileSystemUtilization) * 100

4.query:
timeseries {
  cpu_usage=max(system.cpu.load_average.1m)
},
filter: { host.name == $Hosts }
| fieldsAdd percentage = cpu_usage[] * 100

5.query:
timeseries {
  usage = avg(process.cpu.utilization)
},
by: { process.command_line, host.name, process.pid, state },
filter: { host.name == $Hosts },
from: -2m, to: -1m
| fieldsAdd cpu1 = arrayLast(usage) * 100
| fieldsRemove host.name, usage
| summarize cpu = sum(cpu1), by: {process.pid, process.command_line}
| sort cpu desc

6.query:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name, os.type }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd host.name, os.type
| fieldsRemove timeframe, interval, uptime
| limit 1

11.query:
timeseries {
  paging_usage=avg(system.paging.usage)
}, by: {device, state, host.name}
,filter: { host.name == $Hosts }

13.query:
timeseries {
  count = avg(system.cpu.logical.count)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd value.A = arrayAvg(count)
| fieldsAdd host.name
| fieldsRemove timeframe, interval
| limit 1

14.query:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name, host.arch }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd host.name, host.arch
| fieldsRemove timeframe, interval, uptime
| limit 1

15.query:
timeseries {
  count=avg(system.memory.limit)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd value.A = arrayAvg(count)
| fieldsAdd host.name
| fieldsRemove timeframe, interval
| limit 1

16.query:
timeseries {
  space=max(system.filesystem.usage)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) and mountpoint == "/" }
| fieldsAdd value.A = arrayMax(space)

18.query:
timeseries {
  count=count(system.cpu.physical.count)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd value.A = arrayMax(count)
| fieldsAdd host.name
| fieldsRemove timeframe, interval
| limit 1

24.query:
timeseries {
  uptime=max(system.uptime)
}, by: { host.name }, filter: { matchesValue(host.name, $Hosts) }
| fieldsAdd host.name
| fieldsRemove timeframe, interval, uptime
